mapper.update:
UPDATE EV_EMAIL_VERIFICATION
SET VERIFIED = 'Y'
WHERE EMAIL = #{email}

 	UPDATE PASSWORD_RESET_VERIFICATION
	SET VERIFIED = 'Y'
	WHERE EMAIL = #{email}

mapper.select:
SELECT email, code, expires_at, verified
FROM EV_EMAIL_VERIFICATION
WHERE EMAIL = #{email}

SELECT email, code, expires_at, verified
FROM PASSWORD_RESET_VERIFICATION
WHERE EMAIL = #{email}

SELECT COUNT(*)	
FROM EV_EMAIL_VERIFICATION
WHERE EMAIL = #{email}

SELECT COUNT(*)	
FROM PASSWORD_RESET_VERIFICATION
WHERE EMAIL = #{email}
