  INTO	
  		EV_BOARD_IMAGE(
			  BOARD_NO
			, BOARD_IMAGE
		)
  		VALUES(
			  #{boardNo}
			, #{boardImage}
		)

INSERT
  INTO	
  		EV_DRIVE_ROUTE_IMAGE(
			  BOARD_NO
			, DRIVE_ROUTE_IMAGE
		)
  		VALUES(
			  #{boardNo}
			, #{driveRouteImage}
		)

