mapper.insert:
	INSERT 
	  INTO 
  			EV_CAR_INFO(
			    CAR_NAME
			  , CAR_TYPE
			  , CAR_YEAR
			  , CAR_COMPANY
			  , CAR_BATTERY
			)
  			VALUES(
			    #{carName}
			  , #{carType}
			  , #{carYear}
			  , #{carCompany}
			  , #{carBattery}
			)

mapper.update:
UPDATE
		EV_CAR_INFO
   		SET
			CAR_NAME = #{carName}
		  , CAR_TYPE = #{carType}
		  , CAR_YEAR = #{carYear}
		  , CAR_COMPANY = #{carCompany}
		  , CAR_BATTERY = #{carBattery}

 WHERE
 		CAR_NO = #{carNo}

UPDATE
		EV_CAR_IMAGE
   		SET
			FILE_LOAD = #{fileLoad}
 WHERE
 		CAR_NO = #{carNo}

mapper.select:
SELECT CI.CAR_NO carNo , CI.CAR_NAME carName , CI.CAR_YEAR carYear , CI.CAR_BATTERY carBattery , TO_CHAR(CI.ENROLL_DATE, 'YYYY-MM-DD') returnEnrollDate , CC.COMPANY_NAME companyName , CC.COMPANY_NO companyNo , CT.CAR_TYPE_NAME carTypeName , CT.CAR_TYPE_NO carTypeNo , I.FILE_LOAD fileLoad FROM EV_CAR_INFO CI LEFT JOIN EV_CAR_TYPE CT ON (CI.CAR_TYPE_NO = CT.CAR_TYPE_NO) LEFT JOIN EV_CAR_COMPANY CC ON (CI.COMPANY_NO = CC.COMPANY_NO) LEFT JOIN EV_CAR_IMAGE I ON (I.CAR_NO = CI.CAR_NO) WHERE 1=1
AND CT.CAR_TYPE_NO = #{carTypeNo}
AND CC.COMPANY_NO = #{carCompanyNo}
AND UPPER(CI.CAR_NAME) LIKE '%' || UPPER(#{searchKeyword}) || '%'
ORDER BY ENROLL_DATE DESC
