mapper.insert:
INSERT INTO EV_EVENT VALUES(
	  SEQ_EV_EVENT.NEXTVAL
	, #{ memberNo }
	, #{ eventName }
	, #{ startDate }
	, #{ endDate }
	, #{ eventContent }
	, SYSDATE
	, 'Y'
)

INSERT INTO EV_EVENT_FILE VALUES(
	  SEQ_EV_EVENT.CURRVAL
	, #{ changeFileName }
)

mapper.update:
UPDATE 
		EV_EVENT 
	SET
		  EVENT_NAME = #{ eventName }
		, START_DATE = #{ startDate }
		, END_DATE = #{ endDate }
		, EVENT_CONTENT = #{ eventContent }
WHERE 
	EVENT_NO = #{ eventNo }

UPDATE
		EV_EVENT_FILE
	SET
		FILE_LOAD = #{ filePath }
WHERE EVENT_NO = #{ eventNo }
