mapper.select:
FROM EV_GARAGE G JOIN EV_MEMBER M ON (G.WRITER = M.MEMBER_NO) WHERE 1=1
AND REGION_SIDO = #{regionSido}
AND REGION_SIGUNGU = #{regionSigungu}
AND REGION_DONG = #{regionDong}
AND G.STATUS = 'Y'
AND G.STATUS = 'N'
AND G.ADDRESS LIKE '%' || #{searchKeyword} || '%'
ORDER BY G.ENROLL_DATE ASC

mapper.update:
UPDATE 
		EV_GARAGE 
	SET
		  ALL_ADDRESS = #{allAddress}
		, REGION_SIDO = #{regionSido}
		, REGION_SIGUNGU = #{regionSigungu}
		, REGION_DONG = #{regionDong}
		, ADDRESS = #{address}
		, POST_ADD = #{postAdd}
WHERE 
	GARAGE_NO = #{ garageNo }
