mapper.select:
SELECT IH.RENT_CAR_NO FROM EV_ING_HOTDEAL IH JOIN EV_HOT_DEAL H ON IH.HOT_DEAL_NO = H.HOT_DEAL_NO WHERE IH.RENT_CAR_NO IN
#{carNo}
AND H.START_DATE < #{endDate} AND H.END_DATE > #{startDate}

mapper.update:
UPDATE 
		EV_HOT_DEAL
	SET
		  HOT_DEAL_NAME = #{ hotdealName }
		, DEAL_PERCENT = #{ dealPercent }
		, START_DATE = #{ startDate }
		, END_DATE = #{ endDate }
		, WRITER = #{writer}
WHERE 
	HOT_DEAL_NO = #{ hotdealNo }
