mapper.insert:
	INSERT 
	  INTO 
  			CAR_INFO(
			    CAR_NAME
			  , CAR_TYPE
			  , CAR_YEAR
			  , CAR_COMPANY
			  , CAR_BATTERY
			)
  			VALUES(
			    #{carName}
			  , #{carType}
			  , #{carYear}
			  , #{carCompany}
			  , #{carBattery}
			)

mapper.update:
UPDATE
		CAR_INFO
   		SET(
			CAR_NAME = #{carName}
		  , CAR_TYPE = #{carType}
		  , CAR_YEAR = #{carYear}
		  , CAR_COMPANY = #{carCompany}
		  , CAR_BATTERY = #{carBattery}
		  , ENROLL_DATE = SYSDATE
		)
 WHERE
 		CAR_NO = #{carNo}
