mapper.select:
SELECT * FROM (
SELECT inner_query.*, ROWNUM AS rn FROM (
SELECT RE_NO AS reNo, MEMBER_NO AS memberNo, RE_MEMBER_NO AS reMemberNo, RE_CONTENT AS reContent, RE_ENROLLDATE AS reEnrollDate, RE_STATUS AS reStatus, RE_END AS reEnd, COMMENT_GROUP_NO AS commentGroupNo, COMMENT_DEPTH AS commentDepth FROM EV_RE_COMMENTS01 WHERE MEMBER_NO = #{memberNo} AND ( #{startDate} IS NULL OR #{startDate} = '' OR RE_ENROLLDATE >= TO_DATE(#{startDate}, 'YYYY-MM-DD') ) AND ( #{endDate} IS NULL OR #{endDate} = '' OR RE_ENROLLDATE <= TO_DATE(#{endDate}, 'YYYY-MM-DD') ) AND ( #{title} IS NULL OR #{title} = '' OR RE_CONTENT LIKE '%' || #{title} || '%' ) ORDER BY RE_END DESC ) inner_query WHERE ROWNUM <= #{offset} + #{size} ) WHERE rn > #{offset}
